pragma solidity ^0.8.23;

interface ITrenBoxManager {
    // Enums
    // ------------------------------------------------------------------------------------------------------------

    enum Status {
        nonExistent,
        active,
        closedByOwner,
        closedByLiquidation,
        closedByRedemption
    }

    enum TrenBoxManagerOperation {
        applyPendingRewards,
        liquidateInNormalMode,
        liquidateInRecoveryMode,
        redeemCollateral
    }

    // Events
    // -----------------------------------------------------------------------------------------------------------

    event BaseRateUpdated(address indexed _asset, uint256 _baseRate);
    event LastFeeOpTimeUpdated(address indexed _asset, uint256 _lastFeeOpTime);
    event TotalStakesUpdated(address indexed _asset, uint256 _newTotalStakes);
    event SystemSnapshotsUpdated(
        address indexed _asset, uint256 _totalStakesSnapshot, uint256 _totalCollateralSnapshot
    );
    event LTermsUpdated(address indexed _asset, uint256 _L_Coll, uint256 _L_Debt);
    event TrenBoxSnapshotsUpdated(address indexed _asset, uint256 _L_Coll, uint256 _L_Debt);
    event TrenBoxIndexUpdated(address indexed _asset, address _borrower, uint256 _newIndex);

    event TrenBoxUpdated(
        address indexed _asset,
        address indexed _borrower,
        uint256 _debt,
        uint256 _coll,
        uint256 _stake,
        TrenBoxManagerOperation _operation
    );

    // Custom Errors
    // ----------------------------------------------------------------------------------------------------

    error TrenBoxManager__FeeBiggerThanAssetDraw();
    error TrenBoxManager__OnlyOneTrenBox();

    error TrenBoxManager__OnlyTrenBoxManagerOperations();
    error TrenBoxManager__OnlyBorrowerOperations();
    error TrenBoxManager__OnlyTrenBoxManagerOperationsOrBorrowerOperations();

    // Structs
    // ----------------------------------------------------------------------------------------------------------

    struct TrenBox {
        uint256 debt;
        uint256 coll;
        uint256 stake;
        Status status;
        uint128 arrayIndex;
    }

    // Functions
    // --------------------------------------------------------------------------------------------------------

    function executeFullRedemption(address _asset, address _borrower, uint256 _newColl) external;

    function executePartialRedemption(
        address _asset,
        address _borrower,
        uint256 _newDebt,
        uint256 _newColl,
        uint256 _newNICR,
        address _upperPartialRedemptionHint,
        address _lowerPartialRedemptionHint
    )
        external;

    function getTrenBoxOwnersCount(address _asset) external view returns (uint256);

    function getTrenBoxFromTrenBoxOwnersArray(
        address _asset,
        uint256 _index
    )
        external
        view
        returns (address);

    function getNominalICR(address _asset, address _borrower) external view returns (uint256);

    function getCurrentICR(
        address _asset,
        address _borrower,
        uint256 _price
    )
        external
        view
        returns (uint256);

    function updateStakeAndTotalStakes(
        address _asset,
        address _borrower
    )
        external
        returns (uint256);

    function updateTrenBoxRewardSnapshots(address _asset, address _borrower) external;

    function addTrenBoxOwnerToArray(
        address _asset,
        address _borrower
    )
        external
        returns (uint256 index);

    function applyPendingRewards(address _asset, address _borrower) external;

    function getPendingAssetReward(
        address _asset,
        address _borrower
    )
        external
        view
        returns (uint256);

    function getPendingDebtTokenReward(
        address _asset,
        address _borrower
    )
        external
        view
        returns (uint256);

    function hasPendingRewards(address _asset, address _borrower) external view returns (bool);

    function getEntireDebtAndColl(
        address _asset,
        address _borrower
    )
        external
        view
        returns (
            uint256 debt,
            uint256 coll,
            uint256 pendingDebtTokenReward,
            uint256 pendingAssetReward
        );

    function closeTrenBox(address _asset, address _borrower) external;

    function closeTrenBoxLiquidation(address _asset, address _borrower) external;

    function removeStake(address _asset, address _borrower) external;

    function getRedemptionRate(address _asset) external view returns (uint256);

    function getRedemptionRateWithDecay(address _asset) external view returns (uint256);

    function getRedemptionFeeWithDecay(
        address _asset,
        uint256 _assetDraw
    )
        external
        view
        returns (uint256);

    function getBorrowingRate(address _asset) external view returns (uint256);

    function getBorrowingFee(
        address _asset,
        uint256 _debtTokenAmount
    )
        external
        view
        returns (uint256);

    function getTrenBoxStatus(address _asset, address _borrower) external view returns (uint256);

    function getTrenBoxStake(address _asset, address _borrower) external view returns (uint256);

    function getTrenBoxDebt(address _asset, address _borrower) external view returns (uint256);

    function getTrenBoxColl(address _asset, address _borrower) external view returns (uint256);

    function setTrenBoxStatus(address _asset, address _borrower, uint256 num) external;

    function increaseTrenBoxColl(
        address _asset,
        address _borrower,
        uint256 _collIncrease
    )
        external
        returns (uint256);

    function decreaseTrenBoxColl(
        address _asset,
        address _borrower,
        uint256 _collDecrease
    )
        external
        returns (uint256);

    function increaseTrenBoxDebt(
        address _asset,
        address _borrower,
        uint256 _debtIncrease
    )
        external
        returns (uint256);

    function decreaseTrenBoxDebt(
        address _asset,
        address _borrower,
        uint256 _collDecrease
    )
        external
        returns (uint256);

    function getTCR(address _asset, uint256 _price) external view returns (uint256);

    function checkRecoveryMode(address _asset, uint256 _price) external returns (bool);

    function isValidFirstRedemptionHint(
        address _asset,
        address _firstRedemptionHint,
        uint256 _price
    )
        external
        returns (bool);

    function updateBaseRateFromRedemption(
        address _asset,
        uint256 _assetDrawn,
        uint256 _price,
        uint256 _totalDebtTokenSupply
    )
        external;

    function getRedemptionFee(address _asset, uint256 _assetDraw) external view returns (uint256);

    function finalizeRedemption(
        address _asset,
        address _receiver,
        uint256 _debtToRedeem,
        uint256 _fee,
        uint256 _totalRedemptionRewards
    )
        external;

    function redistributeDebtAndColl(
        address _asset,
        uint256 _debt,
        uint256 _coll,
        uint256 _debtToOffset,
        uint256 _collToSendToStabilityPool
    )
        external;

    function updateSystemSnapshots_excludeCollRemainder(
        address _asset,
        uint256 _collRemainder
    )
        external;

    function movePendingTrenBoxRewardsToActivePool(
        address _asset,
        uint256 _debtTokenAmount,
        uint256 _assetAmount
    )
        external;

    function isTrenBoxActive(address _asset, address _borrower) external view returns (bool);

    function sendGasCompensation(
        address _asset,
        address _liquidator,
        uint256 _debtTokenAmount,
        uint256 _assetAmount
    )
        external;

    function getNetDebt(address _asset, uint256 _debt) external view returns (uint256);
}
","keccak256":"0x183a49a4fefc8f74c19327281dfaa9c88745695efad43225fb8d1411e4c47377","license":"MIT"},"contracts/SortedTrenBoxes.sol":{"content":"// SPDX-License-Identifier: MIT

pragma solidity ^0.8.23;

import { OwnableUpgradeable } from
    "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import { UUPSUpgradeable } from
    "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";

import { ConfigurableAddresses } from "./Dependencies/ConfigurableAddresses.sol";

import { ISortedTrenBoxes } from "./Interfaces/ISortedTrenBoxes.sol";
import { ITrenBoxManager } from "./Interfaces/ITrenBoxManager.sol";

/*
* A sorted doubly linked list with nodes sorted in descending order.
*
* Nodes map to active TrenBoxes in the system - the ID property is the address of a TrenBox owner.
* Nodes are ordered according to their current nominal individual collateral ratio (NICR),
* which is like the ICR but without the price, i.e., just collateral / debt.
*
* The list optionally accepts insert position hints.
*
* NICRs are computed dynamically at runtime, and not stored on the Node. This is because NICRs of
active TrenBoxes
* change dynamically as liquidation events occur.
*
* The list relies on the fact that liquidation events preserve ordering: a liquidation decreases the
NICRs of all active TrenBoxes,
* but maintains their order. A node inserted based on current NICR will maintain the correct
position,
* relative to it's peers, as rewards accumulate, as long as it's raw collateral and debt have not
* changed.
* Thus, Nodes remain sorted by current NICR.
*
* Nodes need only be re-inserted upon a TrenBox operation - when the owner adds or removes
collateral
or debt
* to their position.
*
* The list is a modification of the following audited SortedDoublyLinkedList:
* https://github.com/livepeer/protocol/blob/master/contracts/libraries/SortedDoublyLL.sol
*
*
* Changes made in the Gravita implementation:
*
* - Keys have been removed from nodes
*
* - Ordering checks for insertion are performed by comparing an NICR argument to the current NICR,
calculated at runtime.
*   The list relies on the property that ordering by ICR is maintained as the ETH:USD price varies.
*
* - Public functions with parameters have been made internal to save gas, and given an external
wrapper function for external access
*/
contract SortedTrenBoxes is
    OwnableUpgradeable,
    UUPSUpgradeable,
    ISortedTrenBoxes,
    ConfigurableAddresses
{
    string public constant NAME = "SortedTrenBoxes";

    struct Node {
        bool exists;
        address nextId; // Id of next node (smaller NICR) in the list
        address prevId; // Id of previous node (larger NICR) in the list
    }

    struct TrenBoxesList {
        address head; // Head of the list. Also the node in the list with the largest NICR
        address tail; // Tail of the list. Also the node in the list with the smallest NICR
        uint256 size; // Current size of the list
        mapping(address depositor => Node node) nodes; // Track the corresponding ids for each node
            // in the list
    }

    mapping(address collateral => TrenBoxesList orderedList) public trenBoxes;

    modifier onlyTrenBoxManager() {
        if (msg.sender != trenBoxManager) {
            revert SortedTrenBoxes__CallerMustBeTrenBoxManager();
        }
        _;
    }

    modifier onlyBorrowerOperationsOrTrenBoxManager() {
        if (msg.sender != borrowerOperations && msg.sender != trenBoxManager) {
            revert SortedTrenBoxes__CallerMustBeBorrowerOperationsOrTrenBoxManager();
        }
        _;
    }

    modifier hasNonZeroId(address _id) {
        if (_id == address(0)) {
            revert SortedTrenBoxes__IdCannotBeZeroAddress();
        }
        _;
    }

    modifier hasPositiveNICR(uint256 _NICR) {
        if (_NICR == 0) {
            revert SortedTrenBoxes__NICRMustBeGreaterThanZero();
        }
        _;
    }

    // --- Initializer ---
    function initialize() public initializer {
        address initialOwner = _msgSender();

        __Ownable_init(initialOwner);
        __UUPSUpgradeable_init();
    }

    /*
     * @dev Add a node to the list
     * @param _id Node's id
     * @param _NICR Node's NICR
     * @param _prevId Id of previous node for the insert position
     * @param _nextId Id of next node for the insert position
     */

    function insert(
        address _asset,
        address _id,
        uint256 _NICR,
        address _prevId,
        address _nextId
    )
        external
        override
        onlyBorrowerOperationsOrTrenBoxManager
    {
        _insert(_asset, _id, _NICR, _prevId, _nextId);
    }

    function _insert(
        address _asset,
        address _id,
        uint256 _NICR,
        address _prevId,
        address _nextId
    )
        internal
        hasNonZeroId(_id)
        hasPositiveNICR(_NICR)
    {
        TrenBoxesList storage assetData = trenBoxes[_asset];

        if (_contains(assetData, _id)) {
            revert SortedTrenBoxes__ListAlreadyContainsNode();
        }

        address prevId = _prevId;
        address nextId = _nextId;

        if (!_validInsertPosition(_asset, _NICR, prevId, nextId)) {
            // Sender's hint was not a valid insert position
            // Use sender's hint to find a valid insert position
            (prevId, nextId) = _findInsertPosition(_asset, _NICR, prevId, nextId);
        }

        Node storage node = assetData.nodes[_id];
        node.exists = true;

        if (prevId == address(0) && nextId == address(0)) {
            // Insert as head and tail
            assetData.head = _id;
            assetData.tail = _id;
        } else if (prevId == address(0)) {
            // Insert before `prevId` as the head
            node.nextId = assetData.head;
            assetData.nodes[assetData.head].prevId = _id;
            assetData.head = _id;
        } else if (nextId == address(0)) {
            // Insert after `nextId` as the tail
            node.prevId = assetData.tail;
            assetData.nodes[assetData.tail].nextId = _id;
            assetData.tail = _id;
        } else {
            // Insert at insert position between `prevId` and `nextId`
            node.nextId = nextId;
            node.prevId = prevId;
            assetData.nodes[prevId].nextId = _id;
            assetData.nodes[nextId].prevId = _id;
        }

        assetData.size = assetData.size + 1;
        emit NodeAdded(_asset, _id, _NICR);
    }

    function remove(address _asset, address _id) external override onlyTrenBoxManager {
        _remove(_asset, _id);
    }

    /*
     * @dev Remove a node from the list
     * @param _id Node's id
     */
    function _remove(address _asset, address _id) internal {
        TrenBoxesList storage assetData = trenBoxes[_asset];

        if (!_contains(assetData, _id)) {
            revert SortedTrenBoxer__ListDoesNotContainNode();
        }

        Node storage node = assetData.nodes[_id];
        if (assetData.size > 1) {
            // List contains more than a single node
            if (_id == assetData.head) {
                // The removed node is the head
                // Set head to next node
                assetData.head = node.nextId;
                // Set prev pointer of new head to null
                assetData.nodes[assetData.head].prevId = address(0);
            } else if (_id == assetData.tail) {
                // The removed node is the tail
                // Set tail to previous node
                assetData.tail = node.prevId;
                // Set next pointer of new tail to null
                assetData.nodes[assetData.tail].nextId = address(0);
            } else {
                // The removed node is neither the head nor the tail
                // Set next pointer of previous node to the next node
                assetData.nodes[node.prevId].nextId = node.nextId;
                // Set prev pointer of next node to the previous node
                assetData.nodes[node.nextId].prevId = node.prevId;
            }
        } else {
            // List contains a single node
            // Set the head and tail to null
            assetData.head = address(0);
            assetData.tail = address(0);
        }

        delete assetData.nodes[_id];
        assetData.size = assetData.size - 1;
        emit NodeRemoved(_asset, _id);
    }

    /*
     * @dev Re-insert the node at a new position, based on its new NICR
     * @param _id Node's id
     * @param _newNICR Node's new NICR
     * @param _prevId Id of previous node for the new insert position
     * @param _nextId Id of next node for the new insert position
     */
    function reInsert(
        address _asset,
        address _id,
        uint256 _newNICR,
        address _prevId,
        address _nextId
    )
        external
        override
        onlyBorrowerOperationsOrTrenBoxManager
    {
        if (!contains(_asset, _id)) {
            revert SortedTrenBoxer__ListDoesNotContainNode();
        }

        if (_newNICR == 0) {
            revert SortedTrenBoxes__NICRMustBeGreaterThanZero();
        }

        _remove(_asset, _id);
        _insert(_asset, _id, _newNICR, _prevId, _nextId);
    }

    /*
     * @dev Checks if the list contains a node
     */
    function contains(address _asset, address _id) public view override returns (bool) {
        return trenBoxes[_asset].nodes[_id].exists;
    }

    function _contains(
        TrenBoxesList storage _dataAsset,
        address _id
    )
        internal
        view
        returns (bool)
    {
        return _dataAsset.nodes[_id].exists;
    }

    /*
     * @dev Checks if the list is empty
     */
    function isEmpty(address _asset) public view override returns (bool) {
        return trenBoxes[_asset].size == 0;
    }

    /*
     * @dev Returns the current size of the list
     */
    function getSize(address _asset) external view override returns (uint256) {
        return trenBoxes[_asset].size;
    }

    /*
     * @dev Returns the first node in the list (node with the largest NICR)
     */
    function getFirst(address _asset) external view override returns (address) {
        return trenBoxes[_asset].head;
    }

    /*
     * @dev Returns the last node in the list (node with the smallest NICR)
     */
    function getLast(address _asset) external view override returns (address) {
        return trenBoxes[_asset].tail;
    }

    /*
     * @dev Returns the next node (with a smaller NICR) in the list for a given node
     * @param _id Node's id
     */
    function getNext(address _asset, address _id) external view override returns (address) {
        return trenBoxes[_asset].nodes[_id].nextId;
    }

    /*
     * @dev Returns the previous node (with a larger NICR) in the list for a given node
     * @param _id Node's id
     */
    function getPrev(address _asset, address _id) external view override returns (address) {
        return trenBoxes[_asset].nodes[_id].prevId;
    }

    /*
    * @dev Check if a pair of nodes is a valid insertion point for a new node with the given NICR
     * @param _NICR Node's NICR
     * @param _prevId Id of previous node for the insert position
     * @param _nextId Id of next node for the insert position
     */
    function validInsertPosition(
        address _asset,
        uint256 _NICR,
        address _prevId,
        address _nextId
    )
        external
        view
        override
        returns (bool)
    {
        return _validInsertPosition(_asset, _NICR, _prevId, _nextId);
    }

    function _validInsertPosition(
        address _asset,
        uint256 _NICR,
        address _prevId,
        address _nextId
    )
        internal
        view
        returns (bool)
    {
        if (_prevId == address(0) && _nextId == address(0)) {
            // `(null, null)` is a valid insert position if the list is empty
            return isEmpty(_asset);
        } else if (_prevId == address(0)) {
            // `(null, _nextId)` is a valid insert position if `_nextId` is the head of the list
            return trenBoxes[_asset].head == _nextId
                && _NICR >= ITrenBoxManager(trenBoxManager).getNominalICR(_asset, _nextId);
        } else if (_nextId == address(0)) {
            // `(_prevId, null)` is a valid insert position if `_prevId` is the tail of the list
            return trenBoxes[_asset].tail == _prevId
                && _NICR <= ITrenBoxManager(trenBoxManager).getNominalICR(_asset, _prevId);
        } else {
            // `(_prevId, _nextId)` is a valid insert position if they are adjacent nodes and
            // `_NICR` falls between the two nodes' NICRs
            return trenBoxes[_asset].nodes[_prevId].nextId == _nextId
                && ITrenBoxManager(trenBoxManager).getNominalICR(_asset, _prevId) >= _NICR
                && _NICR >= ITrenBoxManager(trenBoxManager).getNominalICR(_asset, _nextId);
        }
    }

    /*
     * @dev Descend the list (larger NICRs to smaller NICRs) to find a valid insert position
     * @param _trenBoxManager TrenBoxManager contract, passed in as param to save SLOAD’s
     * @param _NICR Node's NICR
     * @param _startId Id of node to start descending the list from
     */
    function _descendList(
        address _asset,
        uint256 _NICR,
        address _startId
    )
        internal
        view
        returns (address, address)
    {
        TrenBoxesList storage assetData = trenBoxes[_asset];

        // If `_startId` is the head, check if the insert position is before the head
        if (
            assetData.head == _startId
                && _NICR >= ITrenBoxManager(trenBoxManager).getNominalICR(_asset, _startId)
        ) {
            return (address(0), _startId);
        }

        address prevId = _startId;
        address nextId = assetData.nodes[prevId].nextId;

        // Descend the list until we reach the end or until we find a valid insert position
        while (prevId != address(0) && !_validInsertPosition(_asset, _NICR, prevId, nextId)) {
            prevId = assetData.nodes[prevId].nextId;
            nextId = assetData.nodes[prevId].nextId;
        }

        return (prevId, nextId);
    }

    /*
     * @dev Ascend the list (smaller NICRs to larger NICRs) to find a valid insert position
     * @param _trenBoxManager TrenBoxManager contract, passed in as param to save SLOAD’s
     * @param _NICR Node's NICR
     * @param _startId Id of node to start ascending the list from
     */
    function _ascendList(
        address _asset,
        uint256 _NICR,
        address _startId
    )
        internal
        view
        returns (address, address)
    {
        TrenBoxesList storage assetData = trenBoxes[_asset];

        // If `_startId` is the tail, check if the insert position is after the tail
        if (
            assetData.tail == _startId
                && _NICR <= ITrenBoxManager(trenBoxManager).getNominalICR(_asset, _startId)
        ) {
            return (_startId, address(0));
        }

        address nextId = _startId;
        address prevId = assetData.nodes[nextId].prevId;

        // Ascend the list until we reach the end or until we find a valid insertion point
        while (nextId != address(0) && !_validInsertPosition(_asset, _NICR, prevId, nextId)) {
            nextId = assetData.nodes[nextId].prevId;
            prevId = assetData.nodes[nextId].prevId;
        }

        return (prevId, nextId);
    }

    /*
     * @dev Find the insert position for a new node with the given NICR
     * @param _NICR Node's NICR
     * @param _prevId Id of previous node for the insert position
     * @param _nextId Id of next node for the insert position
     */
    function findInsertPosition(
        address _asset,
        uint256 _NICR,
        address _prevId,
        address _nextId
    )
        external
        view
        override
        returns (address, address)
    {
        return _findInsertPosition(_asset, _NICR, _prevId, _nextId);
    }

    function _findInsertPosition(
        address _asset,
        uint256 _NICR,
        address _prevId,
        address _nextId
    )
        internal
        view
        returns (address, address)
    {
        address prevId = _prevId;
        address nextId = _nextId;

        if (prevId != address(0)) {
            if (
                !contains(_asset, prevId)
                    || _NICR > ITrenBoxManager(trenBoxManager).getNominalICR(_asset, prevId)
            ) {
                // `prevId` does not exist anymore or now has a smaller NICR than the given NICR
                prevId = address(0);
            }
        }

        if (nextId != address(0)) {
            if (
                !contains(_asset, nextId)
                    || _NICR < ITrenBoxManager(trenBoxManager).getNominalICR(_asset, nextId)
            ) {
                // `nextId` does not exist anymore or now has a larger NICR than the given NICR
                nextId = address(0);
            }
        }

        if (prevId == address(0) && nextId == address(0)) {
            // No hint - descend list starting from head
            return _descendList(_asset, _NICR, trenBoxes[_asset].head);
        } else if (prevId == address(0)) {
            // No `prevId` for hint - ascend list starting from `nextId`
            return _ascendList(_asset, _NICR, nextId);
        } else if (nextId == address(0)) {
            // No `nextId` for hint - descend list starting from `prevId`
            return _descendList(_asset, _NICR, prevId);
        } else {
            // Descend list starting from `prevId`
            return _descendList(_asset, _NICR, prevId);
        }
    }

    function authorizeUpgrade(address newImplementation) public {
        _authorizeUpgrade(newImplementation);
    }

    function _authorizeUpgrade(address) internal override onlyOwner { }
}
