pragma solidity ^0.8.23;

import { IDeposit } from "./IDeposit.sol";

interface IStabilityPool is IDeposit {
    // --- Structs ---

    struct Snapshots {
        mapping(address => uint256) S;
        uint256 P;
        uint256 G;
        uint128 scale;
        uint128 epoch;
    }

    // --- Events ---

    event CommunityIssuanceAddressChanged(address newAddress);
    event DepositSnapshotUpdated(address indexed _depositor, uint256 _P, uint256 _G);
    event SystemSnapshotUpdated(uint256 _P, uint256 _G);

    event AssetSent(address _asset, address _to, uint256 _amount);
    event GainsWithdrawn(
        address indexed _depositor,
        address[] _collaterals,
        uint256[] _amounts,
        uint256 _debtTokenLoss
    );
    event TRENPaidToDepositor(address indexed _depositor, uint256 _TREN);
    event StabilityPoolAssetBalanceUpdated(address _asset, uint256 _newBalance);
    event StabilityPoolDebtTokenBalanceUpdated(uint256 _newBalance);
    event StakeChanged(uint256 _newSystemStake, address _depositor);
    event UserDepositChanged(address indexed _depositor, uint256 _newDeposit);

    event ProductUpdated(uint256 _P);
    event SumUpdated(address _asset, uint256 _S, uint128 _epoch, uint128 _scale);
    event GainsUpdated(uint256 _G, uint128 _epoch, uint128 _scale);
    event EpochUpdated(uint128 _currentEpoch);
    event ScaleUpdated(uint128 _currentScale);

    // --- Errors ---

    error StabilityPool__TrenBoxStorageOnly(address sender, address expected);
    error StabilityPool__AdminContractOnly(address sender, address expected);
    error StabilityPool__TrenBoxManagerOnly(address sender, address expected);
    error StabilityPool__ArrayNotInAscendingOrder();
    error StabilityPool__DebtLossBelowOne(uint256 debtLoss);
    error StabilityPool__DebtLargerThanTotalDeposits();
    error StabilityPool__ProductZero();
    error StabilityPool__AssetsAndAmountsLengthMismatch();
    error StabilityPool__UserHasNoDeposit();
    error StabilityPool__AmountMustBeNonZero();

    // --- Functions ---

    function addCollateralType(address _collateral) external;

    /*
     * Initial checks:
     * - _amount is not zero
     * ---
    * - Triggers a TREN issuance, based on time passed since the last issuance. The TREN issuance is
    shared between *all* depositors.
     * - Sends depositor's accumulated gains (TREN, assets) to depositor
     */
    function provideToSP(uint256 _amount, address[] calldata _assets) external;

    /*
     * Initial checks:
     * - _amount is zero or there are no under collateralized trenBoxes left in the system
     * - User has a non zero deposit
     * ---
    * - Triggers a TREN issuance, based on time passed since the last issuance. The TREN issuance is
    shared between *all* depositors.
     * - Sends all depositor's accumulated gains (TREN, assets) to depositor
     * - Decreases deposit's stake, and takes new snapshots.
     *
     * If _amount > userDeposit, the user withdraws all of their compounded deposit.
     */
    function withdrawFromSP(uint256 _amount, address[] calldata _assets) external;

    /*
    Initial checks:
    * - Caller is TrenBoxManager
    * ---
    * Cancels out the specified debt against the debt token contained in the Stability Pool (as far
    as possible)
    * and transfers the TrenBox's collateral from ActivePool to StabilityPool.
    * Only called by liquidation functions in the TrenBoxManager.
    */
    function offset(uint256 _debt, address _asset, uint256 _coll) external;

    /*
    * Returns debt tokens held in the pool. Changes when users deposit/withdraw, and when TrenBox
    debt is offset.
     */
    function getTotalDebtTokenDeposits() external view returns (uint256);

    /*
     * Calculates the asset gains earned by the deposit since its last snapshots were taken.
     */
    function getDepositorGains(
        address _depositor,
        address[] calldata _assets
    )
        external
        view
        returns (address[] memory, uint256[] memory);

    /*
     * Calculate the TREN gain earned by a deposit since its last snapshots were taken.
     */
    function getDepositorTRENGain(address _depositor) external view returns (uint256);

    /*
     * Return the user's compounded deposits.
     */
    function getCompoundedDebtTokenDeposits(address _depositor) external view returns (uint256);
}
","keccak256":"0x6f317eded874840c88557f214883fc45bfae3d4f4c090171729f6b03a898adec","license":"MIT"},"contracts/Interfaces/ITrenBoxManager.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.23;

interface ITrenBoxManager {
    // Enums
    // ------------------------------------------------------------------------------------------------------------

    enum Status {
        nonExistent,
        active,
        closedByOwner,
        closedByLiquidation,
        closedByRedemption,
        closedByRedistribution
    }

    enum TrenBoxManagerOperation {
        applyPendingRewards,
        liquidateInNormalMode,
        liquidateInRecoveryMode,
        redeemCollateral,
        redistributeCollateral
    }

    // Events
    // -----------------------------------------------------------------------------------------------------------

    event BaseRateUpdated(address indexed _asset, uint256 _baseRate);
    event LastFeeOpTimeUpdated(address indexed _asset, uint256 _lastFeeOpTime);
    event TotalStakesUpdated(address indexed _asset, uint256 _newTotalStakes);
    event SystemSnapshotsUpdated(
        address indexed _asset, uint256 _totalStakesSnapshot, uint256 _totalCollateralSnapshot
    );
    event LTermsUpdated(address indexed _asset, uint256 _L_Coll, uint256 _L_Debt);
    event TrenBoxSnapshotsUpdated(address indexed _asset, uint256 _L_Coll, uint256 _L_Debt);
    event TrenBoxIndexUpdated(address indexed _asset, address _borrower, uint256 _newIndex);

    event TrenBoxUpdated(
        address indexed _asset,
        address indexed _borrower,
        uint256 _debt,
        uint256 _coll,
        uint256 _stake,
        TrenBoxManagerOperation _operation
    );

    // Custom Errors
    // ----------------------------------------------------------------------------------------------------

    error TrenBoxManager__FeeBiggerThanAssetDraw();
    error TrenBoxManager__OnlyOneTrenBox();

    error TrenBoxManager__OnlyTrenBoxManagerOperations();
    error TrenBoxManager__OnlyBorrowerOperations();
    error TrenBoxManager__OnlyTrenBoxManagerOperationsOrBorrowerOperations();

    // Structs
    // ----------------------------------------------------------------------------------------------------------

    struct TrenBox {
        uint256 debt;
        uint256 coll;
        uint256 stake;
        Status status;
        uint128 arrayIndex;
    }

    // Functions
    // --------------------------------------------------------------------------------------------------------

    function executeFullRedemption(address _asset, address _borrower, uint256 _newColl) external;

    function executePartialRedemption(
        address _asset,
        address _borrower,
        uint256 _newDebt,
        uint256 _newColl,
        uint256 _newNICR,
        address _upperPartialRedemptionHint,
        address _lowerPartialRedemptionHint
    )
        external;

    function getTrenBoxOwnersCount(address _asset) external view returns (uint256);

    function getTrenBoxFromTrenBoxOwnersArray(
        address _asset,
        uint256 _index
    )
        external
        view
        returns (address);

    function getNominalICR(address _asset, address _borrower) external view returns (uint256);

    function getCurrentICR(
        address _asset,
        address _borrower,
        uint256 _price
    )
        external
        view
        returns (uint256);

    function updateStakeAndTotalStakes(
        address _asset,
        address _borrower
    )
        external
        returns (uint256);

    function updateTrenBoxRewardSnapshots(address _asset, address _borrower) external;

    function addTrenBoxOwnerToArray(
        address _asset,
        address _borrower
    )
        external
        returns (uint256 index);

    function applyPendingRewards(address _asset, address _borrower) external;

    function getPendingAssetReward(
        address _asset,
        address _borrower
    )
        external
        view
        returns (uint256);

    function getPendingDebtTokenReward(
        address _asset,
        address _borrower
    )
        external
        view
        returns (uint256);

    function hasPendingRewards(address _asset, address _borrower) external view returns (bool);

    function getEntireDebtAndColl(
        address _asset,
        address _borrower
    )
        external
        view
        returns (
            uint256 debt,
            uint256 coll,
            uint256 pendingDebtTokenReward,
            uint256 pendingAssetReward
        );

    function closeTrenBox(address _asset, address _borrower) external;

    function closeTrenBoxLiquidation(address _asset, address _borrower) external;

    function closeTrenBoxRedistribution(
        address _asset,
        address _borrower,
        uint256 _debtTokenGasCompensationToBurn
    )
        external;

    function removeStake(address _asset, address _borrower) external;

    function getRedemptionRate(address _asset) external view returns (uint256);

    function getRedemptionRateWithDecay(address _asset) external view returns (uint256);

    function getRedemptionFeeWithDecay(
        address _asset,
        uint256 _assetDraw
    )
        external
        view
        returns (uint256);

    function getBorrowingRate(address _asset) external view returns (uint256);

    function getBorrowingFee(
        address _asset,
        uint256 _debtTokenAmount
    )
        external
        view
        returns (uint256);

    function getTrenBoxStatus(address _asset, address _borrower) external view returns (uint256);

    function getTrenBoxStake(address _asset, address _borrower) external view returns (uint256);

    function getTrenBoxDebt(address _asset, address _borrower) external view returns (uint256);

    function getTrenBoxColl(address _asset, address _borrower) external view returns (uint256);

    function setTrenBoxStatus(address _asset, address _borrower, uint256 num) external;

    function increaseTrenBoxColl(
        address _asset,
        address _borrower,
        uint256 _collIncrease
    )
        external
        returns (uint256);

    function decreaseTrenBoxColl(
        address _asset,
        address _borrower,
        uint256 _collDecrease
    )
        external
        returns (uint256);

    function increaseTrenBoxDebt(
        address _asset,
        address _borrower,
        uint256 _debtIncrease
    )
        external
        returns (uint256);

    function decreaseTrenBoxDebt(
        address _asset,
        address _borrower,
        uint256 _collDecrease
    )
        external
        returns (uint256);

    function getTCR(address _asset, uint256 _price) external view returns (uint256);

    function checkRecoveryMode(address _asset, uint256 _price) external returns (bool);

    function isValidFirstRedemptionHint(
        address _asset,
        address _firstRedemptionHint,
        uint256 _price
    )
        external
        returns (bool);

    function updateBaseRateFromRedemption(
        address _asset,
        uint256 _assetDrawn,
        uint256 _price,
        uint256 _totalDebtTokenSupply
    )
        external;

    function getRedemptionFee(address _asset, uint256 _assetDraw) external view returns (uint256);

    function finalizeRedemption(
        address _asset,
        address _receiver,
        uint256 _debtToRedeem,
        uint256 _fee,
        uint256 _totalRedemptionRewards
    )
        external;

    function redistributeDebtAndColl(
        address _asset,
        uint256 _debt,
        uint256 _coll,
        uint256 _debtToOffset,
        uint256 _collToSendToStabilityPool
    )
        external;

    function updateSystemSnapshots_excludeCollRemainder(
        address _asset,
        uint256 _collRemainder
    )
        external;

    function movePendingTrenBoxRewardsFromLiquidatedToActive(
        address _asset,
        uint256 _debtTokenAmount,
        uint256 _assetAmount
    )
        external;

    function isTrenBoxActive(address _asset, address _borrower) external view returns (bool);

    function sendGasCompensation(
        address _asset,
        address _liquidator,
        uint256 _debtTokenAmount,
        uint256 _assetAmount
    )
        external;

    function getNetDebt(address _asset, uint256 _debt) external view returns (uint256);
}
