    // -----------------------------------------------------------------------------------------------

    function setAddresses(address[] calldata _addresses) external onlyOwner {
        if (isAddressSetupInitialized) {
            revert ConfigurableAddresses__SetupIsInitialized();
        }
        if (_addresses.length != 13) {
            revert ConfigurableAddresses__LengthMismatch();
        }

        for (uint256 i = 0; i < 13;) {
            if (_addresses[i] == address(0)) {
                revert ConfigurableAddresses__ZeroAddresses(i, _addresses[i]);
            }

            unchecked {
                ++i;
            }
        }

        adminContract = _addresses[0];
        borrowerOperations = _addresses[1];
        debtToken = _addresses[2];
        feeCollector = _addresses[3];
        flashLoanAddress = _addresses[4];
        priceFeed = _addresses[5];
        sortedTrenBoxes = _addresses[6];
        stabilityPool = _addresses[7];
        timelockAddress = _addresses[8];
        treasuryAddress = _addresses[9];
        trenBoxManager = _addresses[10];
        trenBoxManagerOperations = _addresses[11];
        trenBoxStorage = _addresses[12];

        isAddressSetupInitialized = true;
    }

    function setCommunityIssuance(address _communityIssuance) public onlyOwner {
        if (_communityIssuance == address(0)) {
            revert ConfigurableAddresses__CommunityIssuanceZeroAddress();
        }
        communityIssuance = _communityIssuance;
    }

    function setTRENStaking(address _trenStaking) public onlyOwner {
        if (_trenStaking == address(0)) {
            revert ConfigurableAddresses__TRENStakingZeroAddress();
        }
        trenStaking = _trenStaking;
    }
}
","keccak256":"0x20983a5ba6e289bbbb95aeba34057989e87c0818237897c712af27aa8f2c24a4","license":"MIT"},"contracts/FlashLoan.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.23;

import { UUPSUpgradeable } from
    "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import { ReentrancyGuardUpgradeable } from
    "@openzeppelin/contracts-upgradeable/utils/ReentrancyGuardUpgradeable.sol";
import { OwnableUpgradeable } from
    "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import { IERC20 } from "@openzeppelin/contracts/token/ERC20/ERC20.sol";

import { ConfigurableAddresses } from "./Dependencies/ConfigurableAddresses.sol";

import { IAdminContract } from "./Interfaces/IAdminContract.sol";
import { IBorrowerOperations } from "./Interfaces/IBorrowerOperations.sol";
import { IFeeCollector } from "./Interfaces/IFeeCollector.sol";
import { IFlashLoan } from "./Interfaces/IFlashLoan.sol";
import { IFlashLoanReceiver } from "./Interfaces/IFlashLoanReceiver.sol";
import { ITrenBoxManager } from "./Interfaces/ITrenBoxManager.sol";
import { IUniswapRouterV3 } from "./Interfaces/IUniswapRouterV3.sol";
import { IDebtToken } from "./Interfaces/IDebtToken.sol";

/// @title FlashLoan
/// @notice This contract provides functionality for executing flash loans.
contract FlashLoan is
    IFlashLoan,
    ReentrancyGuardUpgradeable,
    OwnableUpgradeable,
    ConfigurableAddresses,
    UUPSUpgradeable
{
    /// @notice The name of contract.
    string public constant NAME = "FlashLoan";

    /// @notice The denominator to calculate fee percentage.
    uint256 public constant FEE_DENOMINATOR = 1000;

    /// @notice The address of Swap Router contract.
    IUniswapRouterV3 public swapRouter;
    /// @notice The address of Stable Coin contract.
    address public stableCoin;

    /// @notice The index that setup is initialized or not.
    bool public isSetupInitialized;

    // ------------------------------------------ Initializer -------------------------------------

    /// @dev Sets an intiial owner for the contract.
    /// @param initialOwner The address of initial owner.
    function initialize(address initialOwner) external initializer {
        __Ownable_init(initialOwner);
        __UUPSUpgradeable_init();
    }

    // ------------------------------------------ Set functions -----------------------------------

    /**
     * @notice Sets addresses for stableCoin and swapRouter contracts.
     * @param _stableCoin The address of Stable Coin contract.
     * @param _swapRouter The address of Swap Router contract.
     */
    function setInternalAddresses(address _stableCoin, address _swapRouter) external onlyOwner {
        if (isSetupInitialized) revert FlashLoan__SetupIsInitialized();
        if (_stableCoin == address(0) || _swapRouter == address(0)) {
            revert FlashLoan__ZeroAddresses();
        }
        stableCoin = _stableCoin;
        swapRouter = IUniswapRouterV3(_swapRouter);

        isSetupInitialized = true;

        emit AddressesSet(_stableCoin, _swapRouter);
    }

    // ------------------------------------------ External functions ------------------------------

    /// @inheritdoc IFlashLoan
    function flashLoan(uint256 _amount) external nonReentrant {
        if (IAdminContract(adminContract).getFlashLoanMinNetDebt() > _amount) {
            revert FlashLoan__AmountBeyondMin();
        }
        if (IAdminContract(adminContract).getFlashLoanMaxNetDebt() < _amount) {
            revert FlashLoan__AmountBeyondMax();
        }

        mintTokens(_amount);
        uint256 balanceBefore = IDebtToken(debtToken).balanceOf(address(this));
        uint256 fee = calculateFee(_amount);

        IDebtToken(debtToken).transfer(msg.sender, _amount);

        IFlashLoanReceiver(msg.sender).executeOperation(_amount, fee, address(debtToken));

        if (IDebtToken(debtToken).balanceOf(address(this)) < balanceBefore + fee) {
            revert FlashLoan__LoanIsNotRepayable();
        }

        burnTokens(_amount);
        sendFeeToCollector();

        emit FlashLoanExecuted(msg.sender, _amount, fee);
    }

    /**
     * @notice Executes a flash loan specifically to repay a debt on the provided asset.
     * @param _asset The address of the asset for which the debt is to be repaid.
     * @dev This function initiates a flash loan transaction to repay a debt on the specified asset.
     */
    function flashLoanForRepay(address _asset) external nonReentrant {
        if (!IAdminContract(adminContract).getIsActive(_asset)) {
            revert FlashLoan__CollateralIsNotActive();
        }
        uint256 debt = ITrenBoxManager(trenBoxManager).getTrenBoxDebt(_asset, msg.sender);
        uint256 gasCompensation = IAdminContract(adminContract).getDebtTokenGasCompensation(_asset);
        uint256 refund = IFeeCollector(feeCollector).simulateRefund(msg.sender, _asset, 1 ether);
        uint256 netDebt = debt - gasCompensation - refund;

        mintTokens(netDebt);

        IDebtToken(debtToken).transfer(msg.sender, netDebt);

        uint256 fee = calculateFee(netDebt);

        IBorrowerOperations(borrowerOperations).repayDebtTokens(
            _asset, netDebt, address(0), address(0)
        );

        uint256 collAmountIn = IERC20(_asset).balanceOf(address(this));
        uint256 debtTokensToGet = netDebt + fee;

        swapTokens(_asset, collAmountIn, debtTokensToGet);

        if (IDebtToken(debtToken).balanceOf(address(this)) < debtTokensToGet) {
            revert FlashLoan__LoanIsNotRepayable();
        }

        burnTokens(netDebt);

        sendFeeToCollector();

        emit FlashLoanExecuted(msg.sender, netDebt, fee);
    }

    /**
     * @notice Gets the current flash loan rate.
     * @return The flash loan fee rate.
     */
    function getFlashLoanRate() external view returns (uint256) {
        return IAdminContract(adminContract).getFlashLoanFee();
    }

    function authorizeUpgrade(address newImplementation) public {
        _authorizeUpgrade(newImplementation);
    }

    function _authorizeUpgrade(address) internal override onlyOwner { }

    // ------------------------------------------ Private functions -------------------------------

    /**
     * @dev Calculates the fee for a given loan amount.
     * @param _amount The amount of the loan.
     * @return The calculated fee for the loan amount.
     */
    function calculateFee(uint256 _amount) private view returns (uint256) {
        uint256 _feeRate = IAdminContract(adminContract).getFlashLoanFee();
        return (_amount * _feeRate) / FEE_DENOMINATOR;
    }

    /// @dev Sends the collected fees to the fee collector.
    function sendFeeToCollector() private {
        address collector = IFeeCollector(feeCollector).getProtocolRevenueDestination();
        uint256 feeAmount = IDebtToken(debtToken).balanceOf(address(this));
        IDebtToken(debtToken).transfer(collector, feeAmount);
    }

    /// @dev Mints the specified amount of debt tokens.
    /// @param _amount The amount of debt tokens to mint.
    function mintTokens(uint256 _amount) private {
        IDebtToken(debtToken).mintFromWhitelistedContract(_amount);
    }

    /// @dev Burns the specified amount of debt tokens.
    /// @param _amount The amount of debt tokens to burn.
    function burnTokens(uint256 _amount) private {
        IDebtToken(debtToken).burnFromWhitelistedContract(_amount);
    }

    /**
     * @dev Swaps as little as possible of one token for `amountOut` of another along the specified
     * path (reversed).
     * @param _tokenIn The address of input collateral.
     * @param _collAmountIn The amount of collateral which will be swapped.
     * @param _debtAmountOut The amount of trenUSD which will (should) be received.
     */
    function swapTokens(address _tokenIn, uint256 _collAmountIn, uint256 _debtAmountOut) private {
        // Approve swapRouter to spend amountInMaximum
        IERC20(_tokenIn).approve(address(swapRouter), _collAmountIn);

        // The tokenIn/tokenOut field is the shared token between the two pools used in the multiple
        // pool swap. In this case stable coin is the "shared" token.
        // For an exactOutput swap, the first swap that occurs is the swap which returns the
        // eventual desired token.
        // In this case, our desired output token is debtToken so that swap happpens first, and is
        // encoded in the path accordingly.
        IUniswapRouterV3.ExactOutputParams memory params = IUniswapRouterV3.ExactOutputParams({
            path: abi.encodePacked(address(debtToken), uint24(3000), stableCoin, uint24(3000), _tokenIn),
            recipient: address(this),
            deadline: block.timestamp,
            amountOut: _debtAmountOut,
            amountInMaximum: _collAmountIn
        });

        // Executes the swap, returning the amountIn actually spent.
        uint256 amountIn = swapRouter.exactOutput(params);

        // If the swap did not require the full _collAmountIn to achieve the exact amountOut then we
        // refund msg.sender and approve the router to spend 0.
        if (amountIn < _collAmountIn) {
            IERC20(_tokenIn).approve(address(swapRouter), 0);
            IERC20(_tokenIn).transfer(msg.sender, _collAmountIn - amountIn);
        }
    }
}
","keccak256":"0xac978481b124adc47d56b6c0568b05cb8b95860ab4f95300b61fc525a054f141","license":"MIT"},"contracts/Interfaces/IAdminContract.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.23;

/**
 * @title IAdminContract
 * @notice Defines the basic interface for AdminContract.
 */
interface IAdminContract {
    /**
     * @dev Struct for storing parameters of a specific collateral asset.
     * @param index The index to map to token address in collateral arrays.
     * @param active The status of collateral asset.
     * @param borrowingFee The one-time fee charged on the loan amount.
     * @param ccr Critical collateral ratio to trigger recovery mode.
     * @param mcr Minimum collateral ratio.
     * @param debtTokenGasCompensation The amount of debt token to be locked
     * on opening TrenBoxes as liquidation reserve.
     * @param minNetDebt Minimum amount of debtToken a TrenBox must have.
     * @param mintCap The total amount of debt token that can be minted.
     * @param percentDivisor The liquidation fee.
     * @param redemptionFeeFloor The floor of redemption fee.
     * @param redemptionBlockTimestamp The timestamp which the redemption can be started from.
     */
    struct CollateralParams {
        uint256 index;
        bool active;
        uint256 borrowingFee;
        uint256 ccr;
        uint256 mcr;
        uint256 debtTokenGasCompensation;
        uint256 minNetDebt;
        uint256 mintCap;
        uint256 percentDivisor;
        uint256 redemptionFeeFloor;
        uint256 redemptionBlockTimestamp;
    }

    /**
     * @dev Struct for storing flash loan parameters.
     * @param flashLoanFee The flash loan fee. (10 = 0,1%, 100 = 10%)
     * @param flashLoanMinDebt The minimum amount of debt token to mint for flash loan.
     * @param flashLoanMaxDebt The maximum amount of debt token to mint for flash loan.
     */
    struct FlashLoanParams {
        uint256 flashLoanFee;
        uint256 flashLoanMinDebt;
        uint256 flashLoanMaxDebt;
    }

    /**
     * @dev Error emitted when the input value is not between min and max values.
     * @param _parameter The label of input parameter.
     * @param _valueEntered The input value.
     * @param _minValue The minimum value.
     * @param _maxValue The maximum value.
     */
    error SafeCheckError(
        string _parameter, uint256 _valueEntered, uint256 _minValue, uint256 _maxValue
    );

    /**
     * @dev Error emitted when the caller is not owner.
     */
    error AdminContract__OnlyOwner();

    /**
     * @dev Error emitted when the caller is not timelock contract.
     */
    error AdminContract__OnlyTimelock();

    /**
     * @dev Error emitted when the collateral asset already exists.
     */
    error AdminContract__CollateralExists();

    /**
     * @dev Error emitted when the collateral asset does not exist.
     */
    error AdminContract__CollateralDoesNotExist();

    /**
     * @dev Error emitted when the collateral asset is not active.
     */
    error AdminContract__CollateralNotConfigured();

    /**
     * @dev Emitted when the collateral asset is added.
     * @param _collateral The address of collateral asset.
     */
    event CollateralAdded(address _collateral);

    /**
     * @dev Emitted when the minimum collateral ratio is updated.
     * @param _oldMCR The old minimum collateral ratio.
     * @param _newMCR The new minimum collateral ratio.
     */
    event MCRChanged(uint256 _oldMCR, uint256 _newMCR);

    /**
     * @dev Emitted when the critical collateral ratio is updated.
     * @param _oldCCR The old critical collateral ratio.
     * @param _newCCR The new critical collateral ratio.
     */
    event CCRChanged(uint256 _oldCCR, uint256 _newCCR);

    /**
     * @dev Emitted when the minimum amount of debt token is updated.
     * @param _oldMinNet The old minimum amount of debt token.
     * @param _newMinNet The new minimum amount of debt token.
     */
    event MinNetDebtChanged(uint256 _oldMinNet, uint256 _newMinNet);

    /**
     * @dev Emitted when the liquidation fee is updated.
     * @param _oldPercentDiv The old liquidation fee.
     * @param _newPercentDiv The new liquidation fee.
     */
    event PercentDivisorChanged(uint256 _oldPercentDiv, uint256 _newPercentDiv);

    /**
     * @dev Emitted when the borrowing fee is updated.
     * @param _oldBorrowingFee The old borrowing fee.
     * @param _newBorrowingFee The new borrowing fee.
     */
    event BorrowingFeeChanged(uint256 _oldBorrowingFee, uint256 _newBorrowingFee);

    /**
     * @dev Emitted when the floor of redemption fee is updated.
     * @param _oldRedemptionFeeFloor The old floor of redemption fee.
     * @param _newRedemptionFeeFloor The new floor of redemption fee.
     */
    event RedemptionFeeFloorChanged(uint256 _oldRedemptionFeeFloor, uint256 _newRedemptionFeeFloor);

    /**
     * @dev Emitted when the mint cap is updated.
     * @param _oldMintCap The old mint cap.
     * @param _newMintCap The new mint cap.
     */
    event MintCapChanged(uint256 _oldMintCap, uint256 _newMintCap);

    /**
     * @dev Emitted when the redemption timestamp of specific collateral is updated.
     * @param _collateral The address of collateral asset.
     * @param _blockTimestamp The new redemption timestamp.
     */
    event RedemptionBlockTimestampChanged(address _collateral, uint256 _blockTimestamp);

    /**
     * @dev Emitted when the flash loan fee is updated.
     * @param _oldFee The old flash loan fee.
     * @param _newFee The new flash loan fee.
     */
    event FlashLoanFeeChanged(uint256 _oldFee, uint256 _newFee);

    /**
     * @dev Emitted when the minimum amount of debt token for flash loan is updated.
     * @param _oldMinDebt The old minimum amount of debt token.
     * @param _newMinDebt The new minimum amount of debt token.
     */
    event FlashLoanMinDebtChanged(uint256 _oldMinDebt, uint256 _newMinDebt);

    /**
     * @dev Emitted when the maximum amount of debt token for flash loan is updated.
     * @param _oldMaxDebt The old minimum amount of debt token.
     * @param _newMaxDebt The new minimum amount of debt token.
     */
    event FlashLoanMaxDebtChanged(uint256 _oldMaxDebt, uint256 _newMaxDebt);

    /// @notice Returns decimal precision, 1 ether.
    function DECIMAL_PRECISION() external pure returns (uint256);

    /// @notice Returns the scaled number which means 100 percent, 1 ether.
    function _100pct() external view returns (uint256);

    /**
     * @notice Adds new collateral asset.
     * @param _collateral The address of collateral asset.
     * @param _debtTokenGasCompensation The amount of debtToken to be locked on opening
     * TrenBoxes as liquidation reserve.
     */
    function addNewCollateral(address _collateral, uint256 _debtTokenGasCompensation) external;

    /**
     * @notice Sets collateral parameters.
     * @param _collateral The address of collateral asset.
     * @param _borrowingFee The one-time fee charged on the loan amount.
     * @param _ccr The critical collateral ratio to trigger recovery mode.
     * @param _mcr The minimum collateral ratio to avoid liquidation under normal mode.
     * @param _minNetDebt The minimum amount of debtToken a TrenBox must have.
     * @param _mintCap The total amount of debt tokens to be allocated.
     * @param _percentDivisor The liquidation fee.
     * @param _redemptionFeeFloor The floor of redemption fee.
     */
    function setCollateralParameters(
        address _collateral,
        uint256 _borrowingFee,
        uint256 _ccr,
        uint256 _mcr,
        uint256 _minNetDebt,
        uint256 _mintCap,
        uint256 _percentDivisor,
        uint256 _redemptionFeeFloor
    )
        external;

    /**
     * @notice Set the status for the specific collateral asset.
     * @param _collateral The address of collateral asset.
     * @param _active The status of collateral; true or false.
     */
    function setIsActive(address _collateral, bool _active) external;
    /**
     * @notice Sets the minimum collateral ratio.
     * @param _collateral The address of collateral asset.
     * @param _newMCR The minimum collateral ratio to avoid liquidations under normal mode.
     */
    function setMCR(address _collateral, uint256 _newMCR) external;

    /**
     * @notice Sets the critical collateral ratio.
     * @param _collateral The address of collateral asset.
     * @param _newCCR The new critical collateral ratio. If the system's total collateral ratio
     * (TCR) falls below the CCR, Recovery Mode is triggered.
     */
    function setCCR(address _collateral, uint256 _newCCR) external;

    /**
     * @notice Sets the minimum amount of debt token to mint when opening a TrenBox.
     * @param _collateral The address of collateral asset.
     * @param _minNetDebt The minimum amount of debt token a TrenBox must have.
     */
    function setMinNetDebt(address _collateral, uint256 _minNetDebt) external;

    /**
     * @notice Sets the liquidation fee.
     * @param _collateral The address of collateral asset.
     * @param _percentDivisor The new :(min 2, max 200).
     */
    function setPercentDivisor(address _collateral, uint256 _percentDivisor) external;

    /**
     * @notice Sets the borrowing fee.
     * @param _collateral The address of collateral asset.
     * @param _borrowingFee The one-time fee charged on the loan amount.
     */
    function setBorrowingFee(address _collateral, uint256 _borrowingFee) external;

    /**
     * @notice Sets the floor of redemption fee.
     * @param _collateral The address of collateral asset.
     * @param _redemptionFeeFloor The floor of redemption fee charged on the redeemed
     * amount(scaled by 1e18); min 0.001(0.1%), max 0.1(10%).
     */
    function setRedemptionFeeFloor(address _collateral, uint256 _redemptionFeeFloor) external;

    /**
     * @notice Sets the total amount of debt tokens that can be allocated.
     * @param _collateral The address of collateral asset.
     * @param _mintCap The mint cap.
     */
    function setMintCap(address _collateral, uint256 _mintCap) external;

    /**
     * @notice Sets the redemption timestamp.
     * @param _collateral The address of collateral asset.
     * @param _blockTimestamp The timestamp which redemption can be started from.
     */
    function setRedemptionBlockTimestamp(address _collateral, uint256 _blockTimestamp) external;

    /**
     * @notice Sets the flash loan fee.
     * @param _flashLoanFee The new flash loan fee.
     */
    function setFeeForFlashLoan(uint256 _flashLoanFee) external;

    /**
     * @notice Sets the minimum amount of debt token to mint for flash loan.
     * @param _flashLoanMinDebt The new minimum amount of debt token.
     */
    function setMinDebtForFlashLoan(uint256 _flashLoanMinDebt) external;

    /**
     * @notice Sets the maximum amount of debt token to mint for flash loan.
     * @param _flashLoanMaxDebt The new maximum amount of debt token.
     */
    function setMaxDebtForFlashLoan(uint256 _flashLoanMaxDebt) external;

    /// @notice Changes the destination where the collected fees go.
    function switchRouteToTRENStaking() external;

    /**
     * @notice Returns the index of a specific collateral which maps to
     * asset address in collateral array.
     * @param _collateral The address of collateral asset.
     */
    function getIndex(address _collateral) external view returns (uint256);

    /**
     * @notice Returns the list of index matched with collateral arrays.
     * @param _colls The arrays of collateral assets.
     */
    function getIndices(address[] memory _colls) external view returns (uint256[] memory);

    /**
     * @notice Returns if a specific collateral is active or not.
     * @param _collateral The address of collateral asset.
     */
    function getIsActive(address _collateral) external view returns (bool);

    /// @notice Returns list of all collateral types in collateral params.
    function getValidCollateral() external view returns (address[] memory);

    /**
     * @notice Returns the minimum collateral ratio of a specific collateral asset.
     * @param _collateral The address of collateral asset.
     */
    function getMcr(address _collateral) external view returns (uint256);

    /**
     * @notice Returns the critical collateral ratio of a specific collateral asset.
     * @param _collateral The address of collateral asset.
     */
    function getCcr(address _collateral) external view returns (uint256);

    /**
     * @notice Returns the amount of debt token to be locked on opening TrenBoxes
     * as liquidation reserve.
     * @param _collateral The address of collateral asset.
     */
    function getDebtTokenGasCompensation(address _collateral) external view returns (uint256);

    /**
     * @notice Returns the minimum amount of debt token a TrenBox must have.
     * @param _collateral The address of collateral asset.
     */
    function getMinNetDebt(address _collateral) external view returns (uint256);

    /**
     * @notice Returns the liquidation fee; min 2, max 200.
     * @param _collateral The address of collateral asset.
     */
    function getPercentDivisor(address _collateral) external view returns (uint256);

    /**
     * @notice Returns the one-time fee charged on the loan amount.
     * @param _collateral The address of collateral asset.
     */
    function getBorrowingFee(address _collateral) external view returns (uint256);

    /**
     * @notice Returns the floor of redemption fee for the specific collateral.
     * @param _collateral The address of collateral asset.
     */
    function getRedemptionFeeFloor(address _collateral) external view returns (uint256);

    /**
     * @notice Returns the redemption timestamp for the specific collateral.
     * @param _collateral The address of collateral asset.
     */
    function getRedemptionBlockTimestamp(address _collateral) external view returns (uint256);

    /**
     * @notice Returns the total allocated amount of debt token for the specific collateral.
     * @param _collateral The address of collateral asset.
     */
    function getMintCap(address _collateral) external view returns (uint256);

    /**
     * @notice Returns the current total amount of debt token for the specific collateral.
     * @param _asset The address of collateral asset.
     */
    function getTotalAssetDebt(address _asset) external view returns (uint256);

    /// @notice Returns the flash loan fee.
    function getFlashLoanFee() external view returns (uint256);

    /// @notice Returns the minimum amount of debt token to mint for flash loan.
    function getFlashLoanMinNetDebt() external view returns (uint256);

    /// @notice Returns the maximum amount of debt token to mint for flash loan.
    function getFlashLoanMaxNetDebt() external view returns (uint256);

    /**
     * @notice Returns if the collected fees go to stakers or treasury.
     * if true, collected fees go to stakers; if false, to the treasury
     */
    function getRouteToTRENStaking() external view returns (bool);
}
","keccak256":"0xd6ed047c9ff47fff091ed7ffaddd9030d828a03a929b2fa8b38a5b6db007363e","license":"MIT"},"contracts/Interfaces/IBorrowerOperations.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.23;

/**
 * @title IBorrowerOperations
 * @notice Defines the basic interface for BorrowerOperations contract.
 */
interface IBorrowerOperations {
    /**
     * @dev Enum for storing the borrower operation type.
     * @param openTrenBox The operation type to open a trenBox.
     * @param closeTrenBox The operation type to close a trenBox.
     * @param adjustTrenBox The operation type to adjust trenBoxes.
     */
    enum BorrowerOperation {
        openTrenBox,
        closeTrenBox,
        adjustTrenBox
    }

    /**
     * @dev Struct for storing collateral and debt changes for a specific asset.
     * @param asset The address of collateral asset.
     * @param isCollIncrease The flag to indicate whether the collateral balance increases or not.
     * @param price The price of collateral asset.
     * @param collChange The change of collateral balance.
     * @param netDebtChange The change of net debt balance.
     * @param debt The current debt balance of a specific collateral asset.
     * @param coll The current collateral balance of a specific collateral asset.
     * @param oldICR The old individual collateral ratio.
     * @param newICR The new individual collateral ratio.
     * @param debtTokenFee The fee amount of debt tokens for borrowing.
     * @param newDebt The updated debt balance of a specific collateral asset.
     * @param newColl The updated collateral balance of a specific collateral asset.
     * @param stake The new stake based on updated collateral balance.
     */
    struct AdjustTrenBox {
        address asset;
        bool isCollIncrease;
        uint256 price;
        uint256 collChange;
        uint256 netDebtChange;
        uint256 debt;
        uint256 coll;
        uint256 oldICR;
        uint256 newICR;
        uint256 newTCR;
        uint256 debtTokenFee;
        uint256 newDebt;
        uint256 newColl;
        uint256 stake;
    }

    /**
     * @dev Struct for storing its information on opening a trenBox.
     * @param asset The address of collateral asset.
     * @param price The price of collateral asset.
     * @param debtTokenFee The fee amount of debt tokens for borrowing.
     * @param newDebt The updated debt balance of a specific collateral asset.
     * @param compositeDebt The composite debt balance including gas compensation.
     * @param ICR The individual collateral ratio.
     * @param NICR The nominal individual collateral ratio.
     * @param stake The new stake on opening new trenBox.
     * @param arrayIndex The index to map to borrower address from owners array.
     */
    struct OpenTrenBox {
        address asset;
        uint256 price;
        uint256 debtTokenFee;
        uint256 netDebt;
        uint256 compositeDebt;
        uint256 ICR;
        uint256 NICR;
        uint256 stake;
        uint256 arrayIndex;
    }

    /**
     * @dev Emitted when the borrowing fee is paid on opening or adjusting a trenBox.
     * @param _asset The address of collateral asset.
     * @param _borrower The address of borrower.
     * @param _feeAmount The amount paid as borrowing fee.
     */
    event BorrowingFeePaid(address indexed _asset, address indexed _borrower, uint256 _feeAmount);

    /**
     * @dev Emitted when a trenBox is created.
     * @param _asset The address of collateral asset.
     * @param _borrower The address of borrower.
     * @param _arrayIndex The index to map to borrower address from owners array.
     */
    event TrenBoxCreated(address indexed _asset, address indexed _borrower, uint256 _arrayIndex);

    /**
     * @dev Emitted when a trenBox is adjusted.
     * @param _asset The address of collateral asset.
     * @param _borrower The address of borrower.
     * @param _debt The new debt balance.
     * @param _coll The new collateral balance.
     * @param _stake The new stake balance.
     * @param _operation The type of borrower's operation.
     */
    event TrenBoxUpdated(
        address indexed _asset,
        address indexed _borrower,
        uint256 _debt,
        uint256 _coll,
        uint256 _stake,
        BorrowerOperation _operation
    );

    /// @dev Error emitted when the specific collateral asset is not active.
    error BorrowerOperations__NotActiveColl();

    /// @dev Error emitted when the specific borrower's trenBox is not active.
    error BorrowerOperations__TrenBoxNotExistOrClosed();

    /// @dev Error emitted when the specific borrower's trenBox is active.
    error BorrowerOperations__TrenBoxIsActive();

    /**
     * @dev Error emitted when the net debt of a specific collateral asset
     * is less than minimum debt amount.
     */
    error BorrowerOperations__TrenBoxNetDebtLessThanMin();

    /// @dev Error emitted when the composite debt amount is zero.
    error BorrowerOperations__CompositeDebtZero();

    /**
     * @dev Error emitted when new individual collateral ratio is below
     * the critical collateral ratio.
     */
    error BorrowerOperations__TrenBoxICRBelowCCR();

    /**
     * @dev Error emitted when new individual collateral ratio is below
     * the minimum collateral ratio.
     */
    error BorrowerOperations__TrenBoxICRBelowMCR();

    /**
     * @dev Error emitted when new individual collateral ratio is below
     * the old ratio.
     */
    error BorrowerOperations__TrenBoxNewICRBelowOldICR();

    /**
     * @dev Error emitted when the total collateral ratio is below
     * the critical collateral ratio.
     */
    error BorrowerOperations__TrenBoxNewTCRBelowCCR();

    /// @dev Error emitted when the debt balance change is zero.
    error BorrowerOperations__ZeroDebtChange();

    /// @dev Error emitted when both collateral top-up and withdrawal changes are made.
    error BorrowerOperations__NotSingularChange();

    /**
     * @dev Error emitted when neither debt change nor collateral top-up nor collateral
     * withdrawal happened.
     */
    error BorrowerOperations__ZeroAdjustment();

    /// @dev Error emitted when a borrowing operation is not allowed in Recovery Mode.
    error BorrowerOperations__OperationInRecoveryMode();

    /// @dev Error emitted when the collateral withdrawal is requested in Recovery Mode.
    error BorrowerOperations__CollWithdrawalInRecoveryMode();

    /// @dev Error emitted when the current debt balance is below repayment amount.
    error BorrowerOperations__InsufficientDebtBalance();

    /// @dev Error emitted when the withdrawal amount is above current collateral balance.
    error BorrowerOperations__InsufficientCollateral();

    /// @dev Error emitted when the total debt amount exceeds mint cap.
    error BorrowerOperations__ExceedMintCap();

    /**
     * @notice Creates a trenBox for the specific collateral asset with requested debt amount.
     * @dev In addition to the requested debt, extra debt is issued to pay the borrowing fee,
     * and cover the gas compensation.
     * @param _asset The address of collateral asset.
     * @param _assetAmount The amount of collateral asset.
     * @param _debtTokenAmount The amount of debt tokens.
     * @param _upperHint Id of previous node for the insert position, used in SortedTrenBoxes.
     * @param _lowerHint Id of next node for the insert position, used in SortedTrenBoxes.
     */
    function openTrenBox(
        address _asset,
        uint256 _assetAmount,
        uint256 _debtTokenAmount,
        address _upperHint,
        address _lowerHint
    )
        external;

    /**
     * @notice Adds collateral to the caller's active trenBox.
     * @param _asset The address of collateral asset.
     * @param _assetSent The amount of collateral asset to add.
     * @param _upperHint Id of previous node for the new insert position, used in SortedTrenBoxes.
     * @param _lowerHint Id of next node for the new insert position, used in SortedTrenBoxes.
     */
    function addColl(
        address _asset,
        uint256 _assetSent,
        address _upperHint,
        address _lowerHint
    )
        external;

    /**
     * @notice Withdraws collateral from the caller's active trenBox.
     * @param _asset The address of collateral asset.
     * @param _collWithdrawal The amount of collateral asset to withdraw.
     * @param _upperHint Id of previous node for the new insert position.
     * @param _lowerHint Id of next node for the new insert position.
     */
    function withdrawColl(
        address _asset,
        uint256 _collWithdrawal,
        address _upperHint,
        address _lowerHint
    )
        external;

    /**
     * @notice Withdraws debt tokens from the caller's trenBox. Mints new debt tokens to the owner,
     * and increase the trenBox's debt accordingly.
     * @param _asset The address of collateral asset.
     * @param _debtTokenAmount The amount of debt token to withdraw.
     * @param _upperHint Id of previous node for the new insert position.
     * @param _lowerHint Id of next node for the new insert position.
     */
    function withdrawDebtTokens(
        address _asset,
        uint256 _debtTokenAmount,
        address _upperHint,
        address _lowerHint
    )
        external;

    /**
     * @notice Repays debt tokens to the caller's trenBox. Burns the repaid debt tokens, and
     * reduces the trenBox's debt accordingly or Closes trenBox if user has enough tokens at all.
     * @param _asset The address of collateral asset.
     * @param _debtTokenAmount The amount of debt token to repay.
     * @param _upperHint Id of previous node for the new insert position.
     * @param _lowerHint Id of next node for the new insert position.
     */
    function repayDebtTokens(
        address _asset,
        uint256 _debtTokenAmount,
        address _upperHint,
        address _lowerHint
    )
        external;

    /**
     * @notice Enables a caller to simultaneously change both their collateral and debt balances.
     * @param _asset The address of collateral asset.
     * @param _assetSent The amount of collateral asset to send.
     * @param _collWithdrawal The amount of collateral asset to withdraw.
     * @param _debtTokenChange The amount of debt token to withdraw or repay.
     * @param _isDebtIncrease The flag to indicate if current operation is withdrawal or repayment.
     * @param _upperHint Id of previous node for the new insert position.
     * @param _lowerHint Id of next node for the new insert position.
     */
    function adjustTrenBox(
        address _asset,
        uint256 _assetSent,
        uint256 _collWithdrawal,
        uint256 _debtTokenChange,
        bool _isDebtIncrease,
        address _upperHint,
        address _lowerHint
    )
        external;

    /**
     * @notice Claims remaining collateral from a redemption or from a liquidation
     * with ICR > MCR in Recovery Mode.
     * @param _asset The address of collateral asset.
     */
    function claimCollateral(address _asset) external;

    /**
     * @notice Returns the composite debt (drawn debt + gas compensation) of a trenBox.
     */
    function getCompositeDebt(address _asset, uint256 _debt) external view returns (uint256);
}
","keccak256":"0x349276e65ed0c0e72ee71a7298343769935dbc3e68d07fcf269448b50325c601","license":"MIT"},"contracts/Interfaces/IDebtToken.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.23;

import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";

/**
 * @title IDebtToken
 * @notice Defines the basic interface for DebtToken contract.
 */
interface IDebtToken is IERC20 {
    /**
     * @dev Emitted when minting debt tokens against specific collateral asset
     * is stopped.
     * @param _asset The address of the collateral asset.
     * @param _state The indicator that shows if minting is stopped or not.
     */
    event EmergencyStopMintingCollateral(address indexed _asset, bool _state);

    /**
     * @dev Emitted when a contract is added to whitelist or removed from it.
     * @param _address The address of contract to add or remove.
     * @param _whitelisted The indicator that shows if contract is added or removed.
     */
    event WhitelistChanged(address indexed _address, bool _whitelisted);

    /**
     * @dev Emitted when BorrowerOperations, StabilityPool, and TrenBoxManager addresses
     * are set.
     * @param _borrowerOperations The address of BorrowerOperations.
     * @param _stabilityPool The address of StabilityPool.
     * @param _trenBoxManager The address of TrenBoxManager.
     */
    event ProtocolContractsAddressesSet(
        address indexed _borrowerOperations,
        address indexed _stabilityPool,
        address indexed _trenBoxManager
    );

    /**
     * @dev Error emitted when a specific collateral has been already blocked from minting.
     * @param _collateral The address of the collateral asset.
     */
    error DebtToken__MintBlockedForCollateral(address _collateral);

    /**
     * @dev Error emitted when zero address is set.
     */
    error DebtToken__InvalidAddressToConnect();

    /**
     * @dev Error emitted when the recipient address is zero address.
     */
    error DebtToken__CannotTransferTokensToZeroAddress();

    /**
     * @dev Error emitted when the recipient address is token contract address itself.
     */
    error DebtToken__CannotTransferTokensToTokenContract();

    /**
     * @dev Error emitted when caller is not whitelisted contract.
     * @param _notWhitelistedContract The address of not whitelisted contract.
     */
    error DebtToken__NotWhitelistedContract(address _notWhitelistedContract);

    /**
     * @dev Error emitted when caller is not BorrowerOperations.
     * @param _caller The address of caller.
     */
    error DebtToken__CallerIsNotBorrowerOperations(address _caller);

    /**
     * @dev Error emitted when caller is not StabilityPool.
     * @param _caller The address of caller.
     */
    error DebtToken__CallerIsNotStabilityPool(address _caller);

    /**
     * @dev Error emitted when caller is neither BorrowerOperations nor StabilityPool.
     * nor TrenBoxManager
     */
    error DebtToken__CannotBurnTokens();

    /**
     * @dev Error emitted when caller is neither StabilityPool nor TrenBoxManager.
     */
    error DebtToken__CannotReturnFromPool();

    /**
     * @notice Allows the whitelisted contracts to mint debt tokens without any collateral.
     * @param _amount The amount to mint debt token.
     */
    function mintFromWhitelistedContract(uint256 _amount) external;

    /**
     * @notice Allows the whitelisted contracts to burn their debt tokens.
     * @param _amount The amount to burn debt token.
     */
    function burnFromWhitelistedContract(uint256 _amount) external;

    /**
     * @notice Allows BorrowerOperations to mint debt tokens to the specific account.
     * @param _asset The address of collateral asset.
     * @param _account The address of account to receive debt tokens.
     * @param _amount The amount to mint debt token.
     */
    function mint(address _asset, address _account, uint256 _amount) external;

    /**
     * @notice Allows BorrowerOperations, TrenBoxManager & StabilityPool to
     * burn debt tokens from the specific account
     * @param _account The address of account to burn debt tokens
     * @param _amount THe amount to burn debt tokens.
     */
    function burn(address _account, uint256 _amount) external;

    /**
     * @notice Transfers the debt tokens from a user to a pool contract.
     * @param _sender The address of account that sends debt tokens.
     * @param _poolAddress The address of pool to receive debt tokens.
     * @param _amount The amount to transfer debt tokens.
     */
    function sendToPool(address _sender, address _poolAddress, uint256 _amount) external;

    /**
     * @notice Sends debt tokens to a user from a pool contract.
     * @param _poolAddress The address of pool that sends debt tokens.
     * @param _receiver The address of account to receive debt tokens.
     * @param _amount The amount to send debt token.
     */
    function returnFromPool(address _poolAddress, address _receiver, uint256 _amount) external;
}
","keccak256":"0x434de19dc7bb101a034928cee065f9ad15ed6ba67b5d6f4784906fac7720c622","license":"MIT"},"contracts/Interfaces/IFeeCollector.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.23;

/**
 * @title IFeeCollector
 * @notice Defines the basic interface for the FeeCollector contract.
 */
interface IFeeCollector {
    /**
     * @dev Struct for storing fee records of specific collateral asset.
     * @param from The timestamp in seconds when the decay of refundable fee started.
     * @param to The timestamp in seconds when the decay of refundable fee expired.
     * @param _amount The amount of refundable fee.
     */
    struct FeeRecord {
        uint256 from;
        uint256 to;
        uint256 amount;
    }

    /**
     * @dev Emitted when the fee record is updated.
     * @param _borrower The address of borrower.
     * @param _asset The address of collateral asset.
     * @param _from The timestamp in seconds when the decay of refundable fee started.
     * @param _to The timestamp in seconds when the decay of refundable fee expired.
     * @param _amount The amount of refundable fee
     */
    event FeeRecordUpdated(
        address indexed _borrower,
        address indexed _asset,
        uint256 _from,
        uint256 _to,
        uint256 _amount
    );

    /**
     * @dev Emitted when the collected fees are transferred to either the treasury
     * or the TRENStaking contract.
     * @param _borrower The address of borrower.
     * @param _asset The address of collateral asset.
     * @param _collector The address of destination.
     * @param _amount The amount of collected fees (debt tokens).
     */
    event FeeCollected(
        address indexed _borrower,
        address indexed _asset,
        address indexed _collector,
        uint256 _amount
    );

    /**
     * @dev Emitted when the fees are refunded to the borrower.
     * @param _borrower The address of borrower.
     * @param _asset The address of collateral asset.
     * @param _amount The amount of refunded fees.
     */
    event FeeRefunded(address indexed _borrower, address indexed _asset, uint256 _amount);

    /**
     * @dev Emitted when the redemption fees are collected.
     * @param _asset The address of collateral asset.
     * @param _amount The amount of collected fees.
     */
    event RedemptionFeeCollected(address indexed _asset, uint256 _amount);

    /**
     * @dev Emitted when the lengths of array parameters are mismatched.
     */
    error FeeCollector__ArrayMismatch();

    /**
     * @dev Emitted when the caller is not BorrowerOperations.
     * @param _sender The address of caller.
     * @param _expected The address of BorrowerOperations.
     */
    error FeeCollector__BorrowerOperationsOnly(address _sender, address _expected);

    /**
     * @dev Emitted when the caller is neither BorrowerOperations nor TrenBoxManager.
     * @param _sender The address of caller.
     * @param _expected1 The address of BorrowerOperations.
     * @param _expected2 The address of TrenBoxManager.
     */
    error FeeCollector__BorrowerOperationsOrTrenBoxManagerOnly(
        address _sender, address _expected1, address _expected2
    );

    /**
     * @dev Emitted when the caller is not TrenBoxManager.
     * @param _sender The address of caller.
     * @param _expected The address of TrenBoxManager.
     */
    error FeeCollector__TrenBoxManagerOnly(address _sender, address _expected);

    /**
     * @dev Emitted when the payback fraction is higher than 1 ether(10**18 wei).
     */
    error FeeCollector__PaybackFractionHigherThanOne();

    /**
     * @dev Emitted when the payback fraction is zero.
     */
    error FeeCollector__ZeroPaybackFraction();

    /**
     * @notice Increases debt of fee amount when a TrenBox is created and again
     * whenever the borrower acquires additional loans.
     * Collects the minimum fee to the platform, for which there is no refund; holds on to the
     * remaining fees until debt is paid, liquidated, or expired.
     * @dev Attention: this method assumes that (debt token) _feeAmount has already been minted and
     * transferred to this contract.
     * @param _borrower The address of borrower.
     * @param _asset The address of collateral asset.
     * @param _feeAmount The fee amount to collect.
     */
    function increaseDebt(address _borrower, address _asset, uint256 _feeAmount) external;

    /**
     * @notice Decreases debt when a TrenBox is adjusted.
     * @param _borrower The address of borrower.
     * @param _asset The address of collateral asset.
     * @param _paybackFraction The amount that the borrower pays back.
     */
    function decreaseDebt(address _borrower, address _asset, uint256 _paybackFraction) external;

    /**
     * @notice Closes debt when it is paid in full.
     * @param _borrower The address of borrower.
     * @param _asset The address of collateral asset.
     */
    function closeDebt(address _borrower, address _asset) external;

    /**
     * @notice Triggered when a TrenBox is liquidated. In that case, all remaining fees are
     * collected by the platform, and no refunds are generated.
     * @param _borrower The address of borrower.
     * @param _asset The address of collateral asset.
     */
    function liquidateDebt(address _borrower, address _asset) external;

    /**
     * @notice Simulates the refund due if a TrenBox would be closed at this moment
     * @dev Helper function used by the UI.
     * @param _borrower The address of borrower.
     * @param _asset The address of collateral asset.
     * @param _paybackFraction The amount that the borrower pays back.
     */
    function simulateRefund(
        address _borrower,
        address _asset,
        uint256 _paybackFraction
    )
        external
        returns (uint256);

    /**
     * @notice Batches collect fees from an array of borrowers and assets.
     * @param _borrowers The address array of borrowers.
     * @param _assets The address array of collateral assets.
     */
    function collectFees(address[] calldata _borrowers, address[] calldata _assets) external;

    /**
     * @notice Sends redemption fee to the protocol revenue destination.
     * @param _asset The address of collateral asset.
     * @param _amount The amount of redemption fee to send.
     */
    function handleRedemptionFee(address _asset, uint256 _amount) external;

    /**
     * @notice Gets the protocol revenue destination.
     */
    function getProtocolRevenueDestination() external view returns (address);
}
